mapper.select:

select a.scenic_name as ScenicName,nvl(m.count,0) count,a.scenicid as scenicCode from
 (
 select sc.scenicid,sc.scenic_name from sys_scenic_user su
 left join sys_scenic sc on su.scenicid = sc.scenicid 
 where su.userid = #{userId}



 		 and sc.provinceid=#{cityId,jdbcType=VARCHAR}




 		 and sc.cityid=#{cityId,jdbcType=VARCHAR}




 		 and sc.countyid=#{cityId,jdbcType=VARCHAR}




 ) a 
 left join 
(
select t.region_id,sum(t.tourists) as count from REGION_SUMMARY_BYDAY t 
where t.stat_day <= (select max(a.stat_day) from REGION_SUMMARY_BYDAY a ) 
 and t.stat_day >= (select max(a.stat_day)-#{timeGap} from REGION_SUMMARY_BYDAY a ) 
 group by t.region_id 
 ) m  on m.region_id=a.scenicid
 order by m.count desc


	select a.scenic_name as ScenicName,nvl(m.sumcount,0) count,a.scenicid as scenicCode from
       (
       select sc.scenicid,sc.scenic_name from sys_scenic_user su
       left join sys_scenic sc on su.scenicid = sc.scenicid 
       where su.userid = #{userId}



            and sc.provinceid=#{cityId,jdbcType=VARCHAR}




            and sc.cityid=#{cityId,jdbcType=VARCHAR}




            and sc.countyid=#{cityId,jdbcType=VARCHAR}



       ) a 
       left join (
       select sum(h.count) as sumcount, h.code as code   
  from holiday_area_scenic  h  where h.area_scenic ='1'
 and holiday_date=to_date(#{holidayTime},'YYYY/MM/DD') group by h.code 
       ) m on m.code=a.scenicid

       order by count  desc
